import React, { useState } from "react";
import Draggable from "react-draggable";
import { ResizableBox } from "react-resizable";
import "react-resizable/css/styles.css";

const DraggableCard: React.FC = () => {
  const [cardText, setCardText] = useState<string>("text");
  const [disableDragging, setDisableDragging] = useState<boolean>(false);
  const [shape, setShape] = useState<"square" | "circle" | "triangle">(
    "square"
  );
  const changeShape = (newShape: "square" | "circle" | "triangle") => {
    setShape(newShape);
  };
  const shapeStyles = {
    square: {},
    circle: {
      borderRadius: "50%",
    },
    triangle: {
      width: "0",
      height: "0",
      borderBottom: "150px solid lightblue",
      borderLeft: "100px solid transparent",
      borderRight: "100px solid transparent",
    },
  };
  const handleTextChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setCardText(event.target.value);
  };

  const handleStart = () => {
    setDisableDragging(true);
  };

  const handleStop = () => {
    setDisableDragging(false);
  };

  return (
   <div>
     <Draggable
      disabled={disableDragging}
      onStart={handleStart}
      onStop={handleStop}
    >
      <ResizableBox
        resizeHandles={["nw", "ne", "sw", "se", "n", "e", "s", "w"]}
        width={200}
        height={150}
        style={{
          ...shapeStyles[shape],
          display: "flex",
          backgroundColor: 'lightblue',
          border: "1px solid blue",
        }}
      >
        <textarea
          value={cardText}
          onChange={handleTextChange}
          style={{
            backgroundColor: "lightblue",
            resize: "none",
            border: "none",
            outline: "none",
            flex: 1,
            textAlign: "center",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        />
      </ResizableBox>
    </Draggable>
    <button onClick={()=>changeShape("square")}/>
    <button onClick={()=>changeShape("circle")}/>
    <button onClick={()=>changeShape("triangle")}/>

   </div>
  
  );
};

export default DraggableCard;
