import React, { Component, RefObject, createRef } from "react";
import DraggableCard from "./DraggableCard";
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import "./PdfViewerWithDraggable.css";

type State = {
  fileType: "pdf" | "xlsx";
  viewerHeight: string;
  selectedShape: "square" | "circle" | "triangle";
};

class DocumentViewer extends Component<{}, State> {
  private docViewerRef: RefObject<HTMLDivElement>;

  constructor(props: {}) {
    super(props);
    this.state = {
      fileType: "pdf",
      viewerHeight: "1800px",
      selectedShape: "square",
    };
    this.docViewerRef = createRef();
  }

  toggleFileType = () => {
    this.setState((prevState) => ({
      fileType: prevState.fileType === "pdf" ? "xlsx" : "pdf",
    }));
  };

  getDocuments = () => {
    if (this.state.fileType === "pdf") {
      return [
        {
          uri: process.env.PUBLIC_URL + "/c4611_sample_explain.pdf",
        },
      ];
    } else if (this.state.fileType === "xlsx") {
      return [
        {
          uri: "https://uploads.codesandbox.io/uploads/user/46ef5051-8a21-46a0-ab94-60d6cd53ab10/jYVg-test-excelaki.xlsx",
        },
      ];
    }
    return [];
  };

  updateViewerHeight = () => {
    if (this.docViewerRef.current) {
      const documentHeight = this.docViewerRef.current.scrollHeight;
      if (this.state.fileType === "pdf") {
        this.setState({
          viewerHeight: `${documentHeight}px`,
        });
      } else if (this.state.fileType === "xlsx") {
        this.setState({
          viewerHeight: "70vh",
        });
      }
    }
  };

  componentDidMount() {
    this.updateViewerHeight();
  }

  componentDidUpdate(prevProps: {}, prevState: State) {
    if (prevState.fileType !== this.state.fileType) {
      this.updateViewerHeight();
    }
  }

  selectShape = (shape: "square" | "circle" | "triangle") => {
    this.setState({ selectedShape: shape });
  };

  render() {
    return (
      <>
        <div className="pdf-viewer-with-draggable">
          <div className="right-panel">
            <DocViewer
              ref={this.docViewerRef as any}
              pluginRenderers={DocViewerRenderers}
              documents={this.getDocuments()}
              config={{
                header: {
                  disableHeader: false,
                  disableFileName: false,
                  retainURLParams: false,
                },
              }}
              style={{ height: this.state.viewerHeight }}
            />
          </div>
          <div className="left-panel">
          <DraggableCard shape={this.state.selectedShape} onSelectShape={this.selectShape} />
          </div>
        </div>
        <footer
          style={{
            position: "sticky",
            bottom: "0",
            backgroundColor: "#f1f1f1",
            textAlign: "center",
            padding: "10px 0",
          }}
        >
          <button onClick={this.toggleFileType}>Chuyển đổi</button>
        </footer>
      </>
    );
  }
}

export default DocumentViewer;
