import React, { useState } from "react";
import Draggable, { DraggableEvent, DraggableData } from "react-draggable";
import { ResizableBox } from "react-resizable";
import "react-resizable/css/styles.css";

export type ShapeType = "square" | "circle" | "triangle";

export const shapeStyles: Record<ShapeType, React.CSSProperties> = {
  square: {},
  circle: {
    borderRadius: "50%",
  },
  triangle: {
    clipPath: "polygon(50% 0%, 0% 100%, 100% 100%)",
  },
};

export const DraggableCard: React.FC = () => {
  const [cardText, setCardText] = useState<string>("text");
  const [disableDragging, setDisableDragging] = useState<boolean>(false);
  const [shape, setShape] = useState<ShapeType>("square");

  const changeShape = (newShape: ShapeType) => {
    setShape(newShape);
  };

  const handleTextChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setCardText(event.target.value);
  };

  const onDraggableStart = () => {
    setDisableDragging(true);
  };
  const onDraggableStop = () => {
    setDisableDragging(false);
  };

  const onStartDragShape = (e: React.DragEvent<HTMLDivElement>) => {
    e.dataTransfer.setData("shape", shape);
    e.dataTransfer.setData("text", cardText);
  };

  const handleDragStartEvent = (e: DraggableEvent, data: DraggableData) => {
    onDraggableStart();
    onStartDragShape(e);
  };
  return (
    <div className="draggable-container">
      <Draggable
        disabled={disableDragging}
        onStart={handleDragStartEvent} 
        onStop={onDraggableStop}
      >
        <ResizableBox
          resizeHandles={["nw", "ne", "sw", "se", "n", "e", "s", "w"]}
          width={200}
          height={150}
          style={{
            ...shapeStyles[shape],
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "lightblue",
            border: "1px solid blue",
          }}
        >
          <textarea
            value={cardText}
            onChange={handleTextChange}
            style={{
              ...shapeStyles[shape],
              backgroundColor: "lightblue",
              resize: "none",
              border: "none",
              outline: "none",
              flex: 1,
              textAlign: "center",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          />
        </ResizableBox>
      </Draggable>
      <div className="button-container">
        <button onClick={() => changeShape("square")}>square</button>
        <button onClick={() => changeShape("circle")}>circle</button>
        <button onClick={() => changeShape("triangle")}>triangle</button>
      </div>
    </div>
  );
};
