import React, { useState } from "react";
import Draggable from "react-draggable";
import { ResizableBox } from "react-resizable";
import "react-resizable/css/styles.css";
import { shapeStyles } from './shapes';


const DraggableCard: React.FC = () => {
  const [cardText, setCardText] = useState<string>("text");
  const [disableDragging, setDisableDragging] = useState<boolean>(false);
  const [shape, setShape] = useState<"square" | "circle" | "triangle">("square");

  const changeShape = (newShape: "square" | "circle" | "triangle") => {
    setShape(newShape);
  };

 
  const handleTextChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setCardText(event.target.value);
  };

  const handleStart = () => {
    setDisableDragging(true);
  };

  const handleStop = () => {
    setDisableDragging(false);
  };


const handleDragStart = (shape: "square" | "circle" | "triangle") => (e: React.DragEvent) => {
  e.dataTransfer.setData("shape", shape);
};


  return (
    <div className="draggable-container">
      <Draggable
        disabled={disableDragging}
        onStart={handleStart}
        onStop={handleStop}
      >
        <ResizableBox
          resizeHandles={["nw", "ne", "sw", "se", "n", "e", "s", "w"]}
          width={200}
          height={150}
          style={{
            ...shapeStyles[shape], 
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "lightblue",
            border: "1px solid blue",
          }}
        >
          <textarea
            value={cardText}
            onChange={handleTextChange}
            style={{
              ...shapeStyles[shape],
              backgroundColor: "lightblue",
              resize: "none",
              border: "none",
              outline: "none",
              flex: 1,
              textAlign: "center",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          />
        </ResizableBox>
      </Draggable>
      <div className="button-container">
        <button draggable onDragStart={handleDragStart("square")} onClick={() => changeShape("square")}>square</button>
        <button draggable onDragStart={handleDragStart("circle")} onClick={() => changeShape("circle")}>circle</button>
        <button draggable onDragStart={handleDragStart("triangle")} onClick={() => changeShape("triangle")}>triangle</button>
      </div>
    </div>
  );
};

export default DraggableCard;
