import React, { Component, createRef, RefObject } from "react";
import Draggable from "react-draggable";
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import { DraggableCard, shapeStyles, ShapeType } from "./DraggableCard";
import "./PdfViewerWithDraggable.css";

type State = {
  fileType: "pdf" | "xlsx";
  viewerHeight: string;
  shapesInDocViewer: Array<{
    shape: string;
    x: number;
    y: number;
    text: string;
  }>;
};

class DocumentViewer extends Component<{}, State> {
  private docViewerRef: RefObject<HTMLDivElement>;

  constructor(props: {}) {
    super(props);
    this.state = {
      fileType: "pdf",
      viewerHeight: "1800px",
      shapesInDocViewer: [],
    };
    this.docViewerRef = createRef();
  }

  toggleFileType = () => {
    this.setState((prevState) => ({
      fileType: prevState.fileType === "pdf" ? "xlsx" : "pdf",
    }));
  };

  getDocuments = () => {
    if (this.state.fileType === "pdf") {
      return [
        {
          uri: process.env.PUBLIC_URL + "/c4611_sample_explain.pdf",
        },
      ];
    } else if (this.state.fileType === "xlsx") {
      return [
        {
          uri: "https://uploads.codesandbox.io/uploads/user/46ef5051-8a21-46a0-ab94-60d6cd53ab10/jYVg-test-excelaki.xlsx",
        },
      ];
    }
    return [];
  };

  updateViewerHeight = () => {
    if (this.docViewerRef.current) {
      const documentHeight = this.docViewerRef.current.scrollHeight;
      if (this.state.fileType === "pdf") {
        this.setState({
          viewerHeight: `${documentHeight}px`,
        });
      } else if (this.state.fileType === "xlsx") {
        this.setState({
          viewerHeight: "70vh",
        });
      }
    }
  };

  componentDidMount() {
    this.updateViewerHeight();
  }

  componentDidUpdate(prevProps: {}, prevState: State) {
    if (prevState.fileType !== this.state.fileType) {
      this.updateViewerHeight();
    }
  }
  // xử lý khi thả các hình vào DocViewer

  handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
    if (!this.docViewerRef.current) return;
    const shape = e.dataTransfer.getData("shape");
    const text = e.dataTransfer.getData("text");
    const x = e.clientX - this.docViewerRef.current.offsetLeft;
    const y = e.clientY - this.docViewerRef.current.offsetTop;

    this.setState((prevState) => ({
      shapesInDocViewer: [
        ...prevState.shapesInDocViewer,
        { shape, x, y, text },
      ],
    }));
  };

  // cập nhật vị trí của hình khi di chuyển
  handleDrag = (e: any, data: any, index: number) => {
    const { x, y } = data;
    this.setState((prevState) => {
      const updatedShapes = [...prevState.shapesInDocViewer];
      updatedShapes[index].x = x;
      updatedShapes[index].y = y;
      return { shapesInDocViewer: updatedShapes };
    });
  };
  
  render() {
    return (
      <>
        <div
          className="pdf-viewer-with-draggable"
          onDrop={this.handleDrop}
          onDragOver={(e) => {
            e.preventDefault();
          }}
        >
          <div className="doc-viewer-container">
            <DocViewer
              ref={this.docViewerRef as any}
              pluginRenderers={DocViewerRenderers}
              documents={this.getDocuments()}
              config={{
                header: {
                  disableHeader: false,
                  disableFileName: false,
                  retainURLParams: false,
                },
              }}
              style={{ height: this.state.viewerHeight }}
            />

            {this.state.shapesInDocViewer.map((shapeInfo, index) => (
              <Draggable
                key={index}
                position={{ x: shapeInfo.x, y: shapeInfo.y }}
                onStop={(e, data) => this.handleDrag(e, data, index)}
              >
                <div
                  style={{
                    ...shapeStyles[shapeInfo.shape as ShapeType],
                    position: "absolute",
                    width: 200,
                    height: 150,
                    backgroundColor: "lightblue",
                    border: "1px solid blue",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  {shapeInfo.text}
                </div>
              </Draggable>
            ))}
          </div>
          <div className="left-panel">
            <DraggableCard />
          </div>
        </div>
        <footer
          style={{
            position: "sticky",
            bottom: "0",
            backgroundColor: "#f1f1f1",
            textAlign: "center",
            padding: "10px 0",
          }}
        >
          <button onClick={this.toggleFileType}>Chuyển đổi</button>
        </footer>
      </>
    );
  }
}

export default DocumentViewer;
